import React from 'react'
import "./style.css"

const Temp = () => {
    const [searchValue,setSearchValue]=React.useState("jabalpur");
    const getWeatherInfo=async()=>{
        try{
            let url=`https://api.openweathermap.org/data/2.5/weather?q=${searchValue}&appid=d0fe59b10061a76d44cdbdf8d2187583`;
            let res=await fetch(url);
            let data=await res.json();
            console.log(data);
            const {temp}=data.main;
            const {main:weathermood}=data.weather[0];
            const {name}=data;
            const{country}=data.sys;
            const myNewWeatherInfo={

                temp,
                weathermood,
                name,
                country
            };


        }catch(error)
        {
            console.log(error);
        }


    };

    React.useEffect(() => {
        getWeatherInfo();
        
        
    }, [])
    return (
        <>
        <div className='wrap'>
            <div className='search'>

                
                <input
                type="search"
                placeholder='search>>>'
                autoFocus
                id='search'
                className='searchTerm' value={searchValue} onChange={(e)=>setSearchValue(e.target.value)}></input>
                
                <button type='button' className='searchButton' onClick={getWeatherInfo}>
                    search
                </button>
                
                
                
            </div>

        </div>
        <article className='widget'>
            <div className='weatherIcon'>
                <i className={"wi wi-day-sunny"}></i>
            </div>
            <div className='weatherInfo'>
                <div className='temperature'>
                    <span>;</span>
                </div>
                <div className='description'>
                    <div className='weatherCondition'>Sunny</div>
                    <div className='place'>Jabalpur,India</div>

                </div>
            </div>
            <div className='date'>{new Date().toLocaleString()}</div>

        </article>
            
        </>
    )
}

export default Temp
